import React, { useEffect, useState } from "react";
import "./styles.css";

function BookCar() {
  const [cars, setCars] = useState([]);
  const [bookingCar, setBookingCar] = useState(null);
  const [customer, setCustomer] = useState({ name: "", contact: "" });

  useEffect(() => {
    fetchCars();
  }, []);

  const fetchCars = async () => {
    try {
      const res = await fetch("http://localhost:8080/api/cars");
      const data = await res.json();
      setCars(data || []);
    } catch (e) {
      console.error(e);
      alert("Could not fetch cars.");
    }
  };

  const openBooking = (car) => {
    setBookingCar(car);
    setCustomer({ name: "", contact: "" });
  };

  const handleBook = async (e) => {
    e.preventDefault();
    if (!bookingCar) return;
    try {
      const res = await fetch("http://localhost:8080/api/bookings", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          carId: bookingCar.id || bookingCar._id,
          ...customer,
        }),
      });
      if (res.ok) {
        alert("‚úÖ Booking confirmed!");
        setBookingCar(null);
        fetchCars();
      } else {
        alert("‚ùå Booking failed.");
      }
    } catch (err) {
      console.error(err);
      alert("‚ö†Ô∏è Server not responding");
    }
  };

  return (
    <div className="page">
      <h2 className="section-title">Available Cars</h2>
      <div className="grid">
        {cars.map((c) => (
          <div key={c.id || c._id} className={`card car-card ${!c.available ? "unavailable" : ""}`}>
            <div className="car-thumb" aria-hidden>
              <div className="placeholder">üöó</div>
            </div>
            <div className="car-body">
              <h3 className="car-name">{c.name}</h3>
              <p className="car-brand">{c.brand}</p>
              <p className="car-price">${c.price}</p>
              <p className="car-availability">
                {c.available ? "Available" : "Not available"}
              </p>
              <div className="card-actions">
                <button
                  className="btn primary"
                  disabled={!c.available}
                  onClick={() => openBooking(c)}
                >
                  Book
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {bookingCar && (
        <div className="modal">
          <div className="card modal-card">
            <h3>Book: {bookingCar.name}</h3>
            <form onSubmit={handleBook} className="form-grid">
              <input
                className="input"
                placeholder="Your name"
                value={customer.name}
                onChange={(e) => setCustomer({ ...customer, name: e.target.value })}
                required
              />
              <input
                className="input"
                placeholder="Contact (email or phone)"
                value={customer.contact}
                onChange={(e) => setCustomer({ ...customer, contact: e.target.value })}
                required
              />
              <div className="actions">
                <button type="submit" className="btn primary">Confirm Booking</button>
                <button type="button" className="btn secondary" onClick={() => setBookingCar(null)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default BookCar;
