import React, { useEffect, useState } from "react";
import './index.css';

function App() {
  const [cars, setCars] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [name, setName] = useState('');

  useEffect(() => {
    fetch("http://localhost:8080/api/cars")
      .then(res => res.json())
      .then(carsData => setCars(carsData))
      .catch(err => console.error('Error fetching cars:', err));

    fetch("http://localhost:8080/api/bookings")
      .then(res => res.json())
      .then(bookingData => setBookings(bookingData))
      .catch(err => console.error('Error fetching bookings:', err));
  }, []);

  const handleBooking = async (carId) => {
    if (!name) { alert('Enter your name'); return; }
    const resp = await fetch('http://localhost:8080/api/bookings', {
      method: 'POST',
      headers: {'Content-Type':'application/json'},
      body: JSON.stringify({ customerName: name, carId })
    });
    if (resp.ok) { alert('Booked!'); window.location.reload(); }
  };

  return (
    <div className="container">
      <h1 className="title">Smart Ride - Car Rental</h1>
      <div className="input-section">
        <input value={name} onChange={e => setName(e.target.value)} placeholder="Enter your name" />
      </div>

      <h2>Available Cars</h2>
      <div className="car-list">
        {cars.length === 0 && <p>No cars yet. Add some via Postman.</p>}
        {cars.map(car => (
          <div key={car.id} className="car-card">
            <h3>{car.name}</h3>
            <p>Brand: {car.brand}</p>
            <p>â‚¹{car.price} / day</p>
            <button onClick={() => handleBooking(car.id)}>Book Now</button>
          </div>
        ))}
      </div>

      <h2>Bookings</h2>
      <ul>
        {Array.isArray(bookings) && bookings.map(b => (
          <li key={b.id}>{b.customerName} - Car ID: {b.carId}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
